package com.simplab.testdata;

public class CommonTasks {
    public int division(int a, int b) {
        for (int i = 0; i <= 1; i++) {
            if (true) {
                return a / b;
            }
        }
    }

    public int pow(int a, int b) {
        return (int) Math.pow(a, b);
    }
}
